INSERT INTO `privacy_policies` (`id`, `description`, `created_at`, `updated_at`) VALUES
(1, '<ul>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">تحرص <strong><u>أكاديمية إديوستيدج</u></strong> على بياناتك الشخصية وكيفية استخدامها فيما لا يضر أي من العملاء.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">كما أننا نهتم بها فى كافة الأوقات ونخضعها لسياسات الخصوصية وما يصاحبها من شروط وأحكام.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">نقدر مخاوفكم واهتمامكم بشأن خصوصية بياناتكم على شبكة الإنترنت.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">لقد تم إعداد هذه السياسة لمساعدتكم في تفهم طبيعة البيانات التي نقوم بتجميعها منكم عند زيارتكم لموقعنا على شبكة الإنترنت وكيفية تعاملنا مع هذه البيانات الشخصية.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">نحن نستخدم بياناتك الشخصية بهدف سهولة التواصل معك، وتلبية طلباتك وأيضًا بهدف تحديد هويتك فيما يتعلق بالخدمات المدفوعة والمجانية التي تقدمها الأكاديمية .</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">إذا أردت تعديل بياناتك الشخصية فى أي وقت راسلنا وسنقوم بتعديلها لك متى شئت.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">من الممكن أن نقوم بتعديل هذه السياسة بين الفترة والأخرى وفقاً لرؤية إدارة الأكاديمية، و هذا بالتأكيد لمصلحتك أولاً و لتعرف كيف نحترم خصوصيتك بالشكل الصحيح، وعند قيامنا بذلك، يتم تحديث سياسة الخصوصية على موقعنا مباشرة مع ذكر تاريخ التحديث وسيحل الإصدار الأخير محل جميع الإصدارات السابقة من هذه السياسة.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">المعلم و الطالب لهم كامل المسؤولية عما يتم عرضه من محتوى مقروء أو مسموع أو مرئي، أكاديمية إديوستيدج غير مسئوله عن إنتهاك المعلم أو الطالب لأي من الحقوق الأدبيه أوالفكريه لأي من المصنفات المقروءه أو المسموعه أو المرئيه والتي يتم عرضها على منصة الأكاديمية.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">لا تتحمل أكاديمية إديوستيدج للتعليم أونلاين أي مسئولية قانونية أو أدبية تنتج عن تعرضك لأي خطر أو مضايقات تنتج عن تبادل معلومات شخصية عن نفسك أو عن الآخرين مثل رقم الهاتف الشخصي أو المنزلي، أو أي حساب على مواقع التواصل الاجتماعي، أو عناوين السكن، وهو ما يخالف الشروط والأحكام السابقة.</span></span></span></li>\r\n</ul>\r\n\r\n<hr />\r\n<p><span dir=\"rtl\"><strong><span style=\"color:#000000\"><span style=\"font-size:18px\"><u>تلتزم الأكاديمية بالآتي:</u></span></span></strong></span></p>\r\n\r\n<ul>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">توفير بيئة إلكترونية سليمة وآمنة للمستخدم.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">عدم بيع البيانات الشخصية لأى جهة مهما كانت ما لم يلزمنا القانون أو أى جهة حكومية بذلك.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">عدم عرض أي إعلانات على موقعنا.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">نستخدم معلوماتك لنوفر لك أفضل خدمات تعليمية وتحسين خدماتنا.</span></span></span></li>\r\n</ul>\r\n\r\n<hr />\r\n<p><span dir=\"rtl\"><strong><span style=\"color:#000000\"><span style=\"font-size:18px\"><u>ما هى المعلومات التى يمكن أن نطلبها منك ؟</u></span></span></strong></span></p>\r\n\r\n<ul>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">اسم ولي الأمر الحقيقي و أسماء الطلبة الحقيقية.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">رقم التليفون.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">عنوان البريد الإلكترونى.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">البلد ومحل الاقامة.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">أكاديمية إديوستيدج للتعليم أونلاين طرف وسيط بين الطالب والمعلم لذلك فنحن غير مسئولين عن خصوصية ما يقدمه المعلم أو الطالب.</span></span></span></li>\r\n	<li><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">قيامك بالتسجيل على موقع إديوستيدج بمثابة موافقة منك على قيام الموقع بحفظ بياناتك ومعالجتها.</span></span></span></li>\r\n</ul>\r\n\r\n<p><span dir=\"rtl\"><span style=\"color:#000000\"><span style=\"font-size:18px\">والله الموفق والمستعان</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:194px; position:absolute; top:545.75px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>', NULL, '2023-07-27 08:34:54');

